import React from 'react'
import Head from 'next/head'
import Link from 'next/link'
import { withRouter } from 'next/router'
import fetch from 'isomorphic-unfetch'
import { CURRENT_URL } from '../utils/consts'
import css from '../styles/global.scss'

const Home = ({}) => (
    <div>
        <Head>
            <title>Home</title>
        </Head>
        <div className="hero">
            <div style={{ textAlign: 'center' }}>
                <ul className={css.html}>
                    <li>
                        <Link href="/HelloWorld" replace>
                            <a>A!=b</a>
                        </Link>
                    </li>
                    <li>
                        <Link href="/HiThere">
                            <img src="logo.jpg" alt="image" />
                        </Link>
                    </li>
                </ul>
            </div>
        </div>
        <style jsx>{`
            a {
                text-decoration: none;
            }
            .hero {
                width: 100%;
                color: #333;
            }
            .hero img {
                cursor: pointer;
            }
            .title {
                margin: 0;
                width: 100%;
                padding-top: 80px;
                line-height: 1.15;
                font-size: 48px;
            }
            .title,
            .description {
                text-align: center;
            }
            .shallow-routing {
                text-align: center;
            }
            .row {
                max-width: 880px;
                margin: 80px auto 40px;
                display: flex;
                flex-direction: row;
                justify-content: space-around;
            }
            .card {
                padding: 18px 18px 24px;
                width: 220px;
                text-align: left;
                text-decoration: none;
                color: #434343;
                border: 1px solid #9b9b9b;
            }
            .card:hover {
                border-color: #067df7;
            }
            .card h3 {
                margin: 0;
                color: #067df7;
                font-size: 18px;
            }
            .card p {
                margin: 0;
                padding: 12px 0 0;
                font-size: 13px;
                color: #333;
            }
        `}</style>
    </div>
)
Home.getInitialProps = async ({ res }) => {
    const response = await fetch(CURRENT_URL + 'api/people')
    const people = await response.json()
    if (res) {
        return { initialPropsCounter: 1, people: people || [] }
    }
    return {}
}
export default withRouter(Home)
